CREATE OR REPLACE FUNCTION man_obter_se_leg_lib_perfil ( nr_seq_estagio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
cd_perfil_w		perfil.cd_perfil%type := obter_perfil_ativo;


BEGIN

begin
select	'N'
into STRICT	ie_retorno_w
from	man_regra_leg_est_perfil b,
	man_regra_legenda_estagio a
where	a.nr_sequencia = b.nr_seq_regra_legenda
and	a.ie_situacao = 'A'
and	a.nr_seq_estagio = nr_seq_estagio_p  LIMIT 1;
exception
when others then
	ie_retorno_w := 'S';
end;

if (ie_retorno_w = 'N') then
	begin
	select	'S'
	into STRICT	ie_retorno_w
	from	man_regra_leg_est_perfil b,
		man_regra_legenda_estagio a
	where	a.nr_sequencia = b.nr_seq_regra_legenda
	and	a.ie_situacao = 'A'
	and	b.cd_perfil = cd_perfil_w
	and	a.nr_seq_estagio = nr_seq_estagio_p  LIMIT 1;
	exception
	when others then
		ie_retorno_w := 'N';
	end;
end if;

return ie_retorno_w;

end;
$body$
