SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_se_mostra_os_confid ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'S';
qt_regra_w			bigint;
nr_seq_confidencialidade_w	bigint;
cd_pessoa_solicitante_w		varchar(10);
cd_pessoa_usuario_w		varchar(10);
cd_cargo_w			bigint;
cd_setor_atendimento_w		bigint;
cd_perfil_w			bigint := obter_perfil_ativo;


BEGIN

if (nr_sequencia_p > 0) then
	select	nr_seq_confidencialidade,
		cd_pessoa_solicitante
	into STRICT	nr_seq_confidencialidade_w,
		cd_pessoa_solicitante_w
	from	man_ordem_servico
	where	nr_sequencia = nr_sequencia_p;

	if (nr_seq_confidencialidade_w > 0) then
		select	count(1)
		into STRICT	qt_regra_w
		from	man_confidencialidade_lib
		where	nr_seq_confidencialidade = nr_seq_confidencialidade_w  LIMIT 1;

		if (qt_regra_w > 0) then
			begin
			select	max(cd_pessoa_fisica),
				max(cd_setor_atendimento)
			into STRICT	cd_pessoa_usuario_w,
				cd_setor_atendimento_w
			from	usuario
			where	nm_usuario = nm_usuario_p;
			/* Independente se existir em alguma regra, o usuário solicitante da OS tem permissão para visualizar */

			if (cd_pessoa_solicitante_w <> coalesce(cd_pessoa_usuario_w,'X')) then
				begin
				begin
				select	cd_cargo
				into STRICT	cd_cargo_w
				from	pessoa_fisica
				where	cd_pessoa_fisica = cd_pessoa_usuario_w;
				exception
				when others then
					cd_cargo_w := '';
				end;

				begin
				select	'S'
				into STRICT	ds_retorno_w
				from (SELECT	1
					from	man_confidencialidade_lib
					where	nr_seq_confidencialidade = nr_seq_confidencialidade_w
					and	cd_cargo = cd_cargo_w
					and	(cd_cargo IS NOT NULL AND cd_cargo::text <> '')
					
union all

					SELECT	1
					from	man_confidencialidade_lib
					where	nr_seq_confidencialidade = nr_seq_confidencialidade_w
					and	cd_pessoa_fisica = cd_pessoa_usuario_w
					and	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '')
					
union all

					select	1
					from	man_confidencialidade_lib
					where	nr_seq_confidencialidade = nr_seq_confidencialidade_w
					and	cd_perfil = cd_perfil_w
					and	(cd_perfil IS NOT NULL AND cd_perfil::text <> '')
					
union all

					select	1
					from	man_confidencialidade_lib
					where	nr_seq_confidencialidade = nr_seq_confidencialidade_w
					and	cd_setor_atendimento = cd_setor_atendimento_w
					and	(cd_setor_atendimento IS NOT NULL AND cd_setor_atendimento::text <> '')) alias4 LIMIT 1;
				exception
				when others then
					ds_retorno_w := 'N';
				end;

				/* Verifica se tem regra por tipo de executor */

				if (ds_retorno_w = 'N') then
					select	count(1)
					into STRICT	qt_regra_w
					from	man_confidencialidade_lib
					where	nr_seq_confidencialidade = nr_seq_confidencialidade_w
					and ((nr_seq_tipo_exec IS NOT NULL AND nr_seq_tipo_exec::text <> '') or coalesce(ie_executor,'N') = 'S');

					if (qt_regra_w > 0) then
						begin
						select	'S'
						into STRICT	ds_retorno_w
						from (SELECT	1
							from	man_confidencialidade_lib b,
								man_ordem_servico_exec a
							where	a.nr_seq_ordem 		= nr_sequencia_p
							and	a.nm_usuario_exec		= nm_usuario_p
							and	b.nr_seq_confidencialidade 	= nr_seq_confidencialidade_w
							and	b.nr_seq_tipo_exec		= a.nr_seq_tipo_exec
							and	(b.nr_seq_tipo_exec IS NOT NULL AND b.nr_seq_tipo_exec::text <> '')
							
union all

							SELECT	1
							from	man_confidencialidade_lib b,
								man_ordem_servico_exec a
							where	a.nr_seq_ordem 		= nr_sequencia_p
							and	a.nm_usuario_exec		= nm_usuario_p
							and	b.nr_seq_confidencialidade 	= nr_seq_confidencialidade_w
							and	coalesce(ie_executor,'N') 		= 'S') alias3 LIMIT 1;
						exception
						when others then
							ds_retorno_w := 'N';
						end;
					end if;
				end if;
				end;
			end if;
			end;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION man_obter_se_mostra_os_confid ( nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

