CREATE OR REPLACE FUNCTION man_obter_se_motivo_lib_func ( nr_seq_ordem_p bigint, nr_seq_motivo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_motivo_protocolo_w	varchar(1);
ds_retorno_w			varchar(1);
cd_funcao_w			integer;


BEGIN

select	max(cd_funcao)
into STRICT	cd_funcao_w
from	man_ordem_servico
where	nr_sequencia = nr_seq_ordem_p;


select 	coalesce(ie_exibe_motivo_protocolo,'S')
into STRICT	ie_exibe_motivo_protocolo_w
from	des_motivo_erro_teste
where	nr_sequencia 	= nr_seq_motivo_p;

if (ie_exibe_motivo_protocolo_w = 'S') then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	teste_funcao
	where	cd_funcao = cd_funcao_w;

elsif (ie_exibe_motivo_protocolo_w = 'N') then

	select 	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ds_retorno_w
	from	teste_funcao
	where	cd_funcao = cd_funcao_w;
else
	ds_retorno_w	:= 'S';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
