SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_se_novo_hist (nr_seq_ordem_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_novo_historico_w	varchar(01) := 'N';
dt_ultimo_visao_w	timestamp;
nr_seq_ordem_serv_w	bigint;


BEGIN

select	max(dt_ult_visao)
into STRICT	dt_ultimo_visao_w
from	man_ordem_servico_exec
where	nr_seq_ordem	= nr_seq_ordem_p
and	nm_usuario_exec	= nm_usuario_p;

if (dt_ultimo_visao_w IS NOT NULL AND dt_ultimo_visao_w::text <> '') then
	begin
	begin
	select	'S'
	into STRICT	ie_novo_historico_w
	from	MAN_ORDEM_SERV_TECNICO
	where	nr_seq_ordem_serv		= nr_seq_ordem_p
	and	dt_historico		> dt_ultimo_visao_w
	and	nm_usuario		<> nm_usuario_p  LIMIT 1;
	exception
	when others then
		ie_novo_historico_w := 'N';
	end;

	end;
else
	begin
	begin
	select	'S'
	into STRICT	ie_novo_historico_w
	from	man_ordem_servico_exec
	where	nm_usuario_exec	= nm_usuario_p
	and	nr_seq_ordem	= nr_seq_ordem_p
	and	coalesce(dt_ult_visao::text, '') = ''  LIMIT 1;
	exception
	when others then
		ie_novo_historico_w := 'N';
	end;
	end;
end if;


return ie_novo_historico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
