CREATE OR REPLACE FUNCTION man_obter_se_ordem_servico_vip ( cd_pessoa_solicitante_p text, nr_grupo_planej_p bigint, nr_grupo_trabalho_p bigint, ie_opcao_p text default 'S') RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(255) := 'N';
cd_cargo_w		bigint;
cd_setor_atend_w		integer;
cd_centro_custo_w		integer;
cd_estabelecimento_w	smallint;
nr_seq_regra_w		man_regra_usuario_vip.nr_sequencia%type;
qt_minuto_fim_w		man_regra_usuario_vip.qt_minuto_fim%type;


BEGIN

if (coalesce(cd_pessoa_solicitante_p,'0') <> '0') then
	begin

	begin
	select	coalesce(b.cd_cargo,0),
		coalesce(a.cd_setor_atendimento,0),
		coalesce(c.cd_centro_custo,0),
		a.cd_estabelecimento
	into STRICT	cd_cargo_w,
		cd_setor_atend_w,
		cd_centro_custo_w,
		cd_estabelecimento_w
	FROM pessoa_fisica b, usuario a
LEFT OUTER JOIN setor_atendimento c ON (a.cd_setor_atendimento = c.cd_setor_atendimento)
WHERE b.cd_pessoa_fisica = a.cd_pessoa_fisica  and b.cd_pessoa_fisica = cd_pessoa_solicitante_p and a.ie_situacao = 'A'   LIMIT 1;
	exception
	when others then
		cd_cargo_w		:= 0;
		cd_setor_atend_w		:= 0;
		cd_centro_custo_w		:= 0;
		cd_estabelecimento_w	:= 0;
	end;

	begin
	select	nr_sequencia,
		coalesce(qt_minuto_fim,0)
	into STRICT	nr_seq_regra_w,
		qt_minuto_fim_w
	from	man_regra_usuario_vip a
	where	coalesce(a.cd_pessoa_fisica,cd_pessoa_solicitante_p) = cd_pessoa_solicitante_p
	and	coalesce(a.cd_cargo,cd_cargo_w) = cd_cargo_w
	and	coalesce(a.cd_setor_atendimento,cd_setor_atend_w) = cd_setor_atend_w
	and	coalesce(a.cd_centro_custo,cd_centro_custo_w) = cd_centro_custo_w
	and	coalesce(a.cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and	a.ie_situacao = 'A'
	and (not exists (	SELECT	1
				from	man_regra_usuario_vip_lib x
				where	x.nr_seq_regra = a.nr_sequencia)
		or exists (		SELECT	1
				from	man_regra_usuario_vip_lib x
				where	x.nr_seq_regra = a.nr_sequencia
				and	coalesce(x.nr_seq_grupo_planej,coalesce(nr_grupo_planej_p,0)) = coalesce(nr_grupo_planej_p,0)
				and	coalesce(x.nr_seq_grupo_trab,coalesce(nr_grupo_trabalho_p,0)) = coalesce(nr_grupo_trabalho_p,0)))  LIMIT 1;
	exception
	when others then
		ie_retorno_w := 'N';
	end;

	if (coalesce(nr_seq_regra_w,0) > 0) then
		if (ie_opcao_p = 'S') then
			ie_retorno_w	:= 'S';
		elsif (ie_opcao_p = 'Q') then
			ie_retorno_w	:= qt_minuto_fim_w;
		end if;
	end if;


	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
