CREATE OR REPLACE FUNCTION man_obter_se_ordem_serv_filha ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01) := 'N';


BEGIN

	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	man_ordem_servico a
	where	a.nr_sequencia = nr_sequencia_p
	and		(a.nr_seq_ordem_serv_pai IS NOT NULL AND a.nr_seq_ordem_serv_pai::text <> '');
			
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
