CREATE OR REPLACE FUNCTION man_obter_se_os_em_exec (nr_seq_ordem_serv_p bigint, nm_usuario_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_ordem_w	integer;


BEGIN

select	count(1)
into STRICT	qt_ordem_w
from	man_ordem_serv_ativ
where	nr_seq_ordem_serv 	= nr_seq_ordem_serv_p
and	nm_usuario_exec		= nm_usuario_p
and	coalesce(dt_fim_atividade::text, '') = ''  LIMIT 1;

if (qt_ordem_w > 0) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w := 'N';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
