CREATE OR REPLACE FUNCTION man_obter_se_os_orc_aprov (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w		bigint;
ds_retorno_w		varchar(1) := 'N';

BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	count(*)
	into STRICT	qt_existe_w
	from	man_ordem_servico_orc
	where	nr_seq_ordem = nr_sequencia_p
	and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');

	if (qt_existe_w > 0) then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
