SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_se_pessoa_ccb ( nr_seq_equipe_p bigint, nr_seq_grupo_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qt_reg_w	bigint;
cd_cargo_w	bigint;
ie_tipo_equipe_w varchar(10);


BEGIN


if (coalesce(cd_pessoa_fisica_p::text, '') = '') or (coalesce(nr_seq_equipe_p::text, '') = '') then
	return 'N';
end if;

select	coalesce(max(a.ie_tipo_equipe),'X')
into STRICT	ie_tipo_equipe_w
from	ccb_equipe a
where	a.nr_sequencia = nr_seq_equipe_p;

--if ((ie_tipo_equipe_w = 'SA') or (ie_tipo_equipe_w = 'BA')) then	
if (ie_tipo_equipe_w = 'SA') then	
	select	count(1)
	into STRICT	qt_reg_w
	from	ccb_grupo_desenv_v a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and		a.nr_seq_grupo		= nr_seq_grupo_p;	
elsif (ie_tipo_equipe_w = 'ES') then
	select	count(1)
	into STRICT	qt_reg_w
	from	ccb_especialista_v a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and		a.nr_seq_grupo		= nr_seq_grupo_p;	
else
	select	count(1)
	into STRICT	qt_reg_w
	from	ccb_equipe_regra a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	a.nr_seq_equipe		= nr_seq_equipe_p
	and	trunc(clock_timestamp())	between trunc(a.dt_inicio) and fim_dia(trunc(coalesce(a.DT_FIM,clock_timestamp())));

	if (qt_reg_w	> 0) then
		return 'S';
	end if;

	select	max(a.cd_cargo)
	into STRICT	cd_cargo_w
	from	pessoa_fisica a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;

	select	count(1)
	into STRICT	qt_reg_w
	from	ccb_equipe_regra a
	where	a.cd_cargo	= cd_cargo_w
	and	a.nr_seq_equipe		= nr_seq_equipe_p
	and	trunc(clock_timestamp())	between trunc(a.dt_inicio) and fim_dia(trunc(coalesce(a.DT_FIM,clock_timestamp())));
end if;

if (qt_reg_w	> 0) then
	return 'S';
end if;


return	'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION man_obter_se_pessoa_ccb ( nr_seq_equipe_p bigint, nr_seq_grupo_p bigint, cd_pessoa_fisica_p text) FROM PUBLIC;

