CREATE OR REPLACE FUNCTION man_obter_se_pessoa_sme (nr_seq_sme_equipe_p sme_equipe_regra.nr_seq_sme_equipe%type, cd_pessoa_fisica_p sme_equipe_regra.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


qt_reg_w			bigint;
cd_cargo_w			bigint;
ie_tipo_equipe_w	varchar(10);
result_w			varchar(1) := 'N';


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND nr_seq_sme_equipe_p IS NOT NULL AND nr_seq_sme_equipe_p::text <> '') then
	begin
		select	count(1)
		into STRICT	qt_reg_w
		from	sme_equipe_regra a
		where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and		a.nr_seq_sme_equipe	= nr_seq_sme_equipe_p
		and		coalesce(a.ie_situacao, 'A')	= 'A';

		if (qt_reg_w > 0) then
			result_w := 'S';
		end if;
	end;
end if;

return result_w;

end;
