CREATE OR REPLACE FUNCTION man_obter_se_pf_aprov_os ( nr_sequencia_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* Opção
O - Aprovação OS
P - Aprovação PF */
ds_retorno_w	varchar(1) := 'N';


BEGIN

if (ie_opcao_p = 'O') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	man_ordem_servico_aprov
	where	nr_seq_ordem_serv = nr_sequencia_p
	and	cd_pessoa_aprov = coalesce(cd_pessoa_fisica_p,cd_pessoa_aprov);
elsif (ie_opcao_p = 'P') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	man_ordem_servico_aprov
	where	nr_seq_ordem_serv = nr_sequencia_p
	and	cd_pessoa_aprov = cd_pessoa_fisica_p
	and	coalesce(dt_aprovacao::text, '') = '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
