CREATE OR REPLACE FUNCTION man_obter_se_tipo_ativ_lib ( nm_usuario_p text, nr_seq_tipo_ativ_p bigint, nr_seq_grupo_trab_p bigint, nr_seq_grupo_planej_p bigint, cd_setor_atendimento_p bigint, ie_tipo_atividade_p text) RETURNS varchar AS $body$
DECLARE


qt_existe_w		bigint;
cd_perfil_w		bigint := obter_perfil_ativo;
ds_retorno_w		varchar(2) := 'S';


BEGIN
/*
ie_tipo_atividade_p
P = Prevista
R = Realizada
*/
if (coalesce(nr_seq_tipo_ativ_p,0) > 0) then
	begin
	/*Se não existe regra está liberado*/

	select	count(*)
	into STRICT	qt_existe_w
	from	man_tipo_ativ_lib b,
		man_tipo_ativ a
	where	a.nr_sequencia = b.nr_seq_tipo_ativ
	and	a.ie_tipo_atividade = ie_tipo_atividade_p
	and	b.nr_seq_tipo_ativ = nr_seq_tipo_ativ_p;

	if (qt_existe_w > 0) then
		begin

		select ie_visualiza
		into STRICT	ds_retorno_w
		from (
			SELECT	ie_visualiza
			from	man_tipo_ativ_lib
			where	nr_seq_tipo_ativ = nr_seq_tipo_ativ_p
			and (coalesce(cd_perfil,coalesce(cd_perfil_w,0)) = coalesce(cd_perfil_w,0))
			and (coalesce(nr_seq_grupo_trab,coalesce(nr_seq_grupo_trab_p,0)) = coalesce(nr_seq_grupo_trab_p,0))
			and (coalesce(nr_seq_grupo_planej,coalesce(nr_seq_grupo_planej_p,0)) = coalesce(nr_seq_grupo_planej_p,0))
			and (coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0))
			order by
				coalesce(nr_seq_grupo_trab,0) desc,
				coalesce(nr_seq_grupo_planej,0) desc,
				coalesce(cd_setor_atendimento,0) desc,
				coalesce(cd_perfil,0) desc,
				nr_sequencia
			) alias21 LIMIT 1;

		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
	end;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
