CREATE OR REPLACE FUNCTION man_obter_se_tipo_grupo_trab ( nr_seq_tipo_p bigint, nr_grupo_trabalho_p bigint, nr_grupo_planej_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(01) := 'S';
qt_existe_w	bigint;

c01 CURSOR FOR
SELECT	'S'
from	man_tipo_ordem_serv_lib
where	nr_seq_tipo_ordem_serv				= nr_seq_tipo_p
and	coalesce(nr_seq_grupo_trabalho,nr_grupo_trabalho_p)		= nr_grupo_trabalho_p
and	coalesce(nr_seq_grupo_planejamento,nr_grupo_planej_p)	= nr_grupo_planej_p
order by coalesce(nr_seq_grupo_planejamento,0),
	coalesce(nr_seq_grupo_trabalho,0);


BEGIN
select	count(*)
into STRICT	qt_existe_w
from	man_tipo_ordem_serv_lib
where	nr_seq_tipo_ordem_serv	= nr_seq_tipo_p;

if (qt_existe_w > 0) then
	ds_retorno_w := 'N';

	open C01;
	loop
	fetch C01 into
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;
end if;

return	ds_retorno_w;

end;
$body$
