CREATE OR REPLACE FUNCTION man_obter_se_tipo_hist_setor ( nr_seq_tipo_p bigint, cd_setor_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(01)	:= 'S';
ds_retorno_deleg_w		varchar(1)		:= 'N';
qt_regra_w			bigint;
cd_cargo_w			bigint;
nr_seq_grupo_des_w		bigint;
nm_usuario_w			varchar(15);
ie_subst_analista_desenv_w		varchar(15);

c01 CURSOR FOR
SELECT	ie_visualiza
from	man_tipo_hist_setor
where	nr_seq_tipo_hist = nr_seq_tipo_p
and	coalesce(cd_setor_atendimento,cd_setor_p)	= cd_setor_p
and	coalesce(cd_cargo,cd_cargo_w)		= cd_cargo_w
and	coalesce(nm_usuario_regra, nm_usuario_w)		= nm_usuario_w
order by	cd_cargo desc,
	cd_setor_atendimento desc,
	nr_sequencia;

c02 CURSOR FOR
SELECT	ie_visualiza
from	man_tipo_hist_setor a
where	nr_seq_tipo_hist = nr_seq_tipo_p
and	coalesce(cd_setor_atendimento,cd_setor_p)	= cd_setor_p
and	exists (	select	1
				from	man_ordem_serv_deleg d
				where	a.cd_cargo = d.cd_cargo
				and		coalesce(a.nm_usuario_regra, nm_usuario_w) = d.nm_usuario_deleg
				and		clock_timestamp() between pkg_date_utils.start_of(dt_inicio_vigencia,'DAY') and pkg_date_utils.end_of(coalesce(dt_fim_vigencia, clock_timestamp()), 'DAY'))
and	coalesce(nm_usuario_regra, nm_usuario_w)		= nm_usuario_w
order by	cd_cargo desc,
	cd_setor_atendimento desc,
	nr_sequencia;


BEGIN
select	count(*)
into STRICT	qt_regra_w
from	man_tipo_hist_setor
where	nr_seq_tipo_hist	= nr_seq_tipo_p;

nm_usuario_w	:= wheb_usuario_pck.get_nm_usuario;
cd_cargo_w	:= coalesce(Obter_Dados_Usuario_Opcao(nm_usuario_w,'R'),0);

if (qt_regra_w > 0) then
	open C01;
	loop
	fetch C01 into
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
end if;

if (user = 'CORP') and (ds_retorno_w in ('N', 'V')) then
	open C02;
	loop
	fetch C02 into
		ds_retorno_deleg_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	end loop;
	close C02;

	if (ds_retorno_deleg_w <> 'N') then
		ds_retorno_w := ds_retorno_deleg_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
