CREATE OR REPLACE FUNCTION man_obter_se_usuario_lib_grupo ( nr_seq_grupo_planej_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

ie_retorno_w		varchar(1) := 'S';
qt_registro_w		bigint;
cd_cargo_w		bigint;

BEGIN
select	count(*) 
into STRICT	qt_registro_w 
from	man_grupo_planej_usuario 
where	nr_seq_grupo_planej = nr_seq_grupo_planej_p;
if (qt_registro_w > 0) then 
	begin 
	 
	select	obter_cargo_PF(cd_pessoa_fisica,'C') 
	into STRICT	cd_cargo_w 
	from	usuario 
	where	nm_usuario = nm_usuario_p;
	 
	select	'S' 
	into STRICT	ie_retorno_w 
	from	man_grupo_planej_usuario 
	where	nr_seq_grupo_planej	= nr_seq_grupo_planej_p 
	and (nm_usuario_param		= nm_usuario_p or 
		cd_cargo			= cd_cargo_w)  LIMIT 1;
	exception 
	when others then 
		ie_retorno_w := 'N';
	end;
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
