CREATE OR REPLACE FUNCTION man_obter_se_usu_subst_gr_trab ( nm_usuario_p text, nr_seq_grupo_trab_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';


BEGIN

if (coalesce(nm_usuario_p,'X') <> 'X') and (coalesce(nr_seq_grupo_trab_p,0) <> 0) then
	begin

	begin
	select	'S'
	into STRICT	ds_retorno_w
	from	man_grupo_trab_substit
	where	nm_usuario_substituto = nm_usuario_p
	and	nr_seq_grupo_trabalho = nr_seq_grupo_trab_p
	and	trunc(clock_timestamp()) between trunc(coalesce(dt_inicio_vigencia,clock_timestamp())) and trunc(coalesce(dt_fim_vigencia,clock_timestamp()))
	and	coalesce(ie_dia_semana::text, '') = ''  LIMIT 1;
	exception
	when others then
		ds_retorno_w := 'N';
	end;

	if (ds_retorno_w = 'N') then
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	man_grupo_trab_substit
		where	nm_usuario_substituto = nm_usuario_p
		and	nr_seq_grupo_trabalho = nr_seq_grupo_trab_p
		and	pkg_date_utils.get_WeekDay(clock_timestamp()) = ie_dia_semana
		and	to_date(to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi'),'dd/mm/yy hh24:mi') between
				CASE WHEN coalesce(hr_inicio::text, '') = '' THEN to_date(to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi'),'dd/mm/yy hh24:mi')  ELSE to_date(to_char(clock_timestamp(),'dd/mm/yyyy') || ' ' || substr(hr_inicio,1,2) || ':' || substr(hr_inicio,3,4),'dd/mm/yy hh24:mi') END  and
				CASE WHEN coalesce(hr_fim::text, '') = '' THEN to_date(to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi'),'dd/mm/yy hh24:mi')  ELSE to_date(to_char(clock_timestamp(),'dd/mm/yyyy') || ' ' || substr(hr_fim,1,2) || ':' || substr(hr_fim,3,4),'dd/mm/yy hh24:mi') END
		and	coalesce(dt_inicio_vigencia::text, '') = ''
		and	coalesce(dt_fim_vigencia::text, '') = ''  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
