CREATE OR REPLACE FUNCTION man_obter_situacao_cliente (nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
cd_cnpj_cliente_w	varchar(14);


BEGIN

select		obter_dados_com_cliente(nr_seq_cliente_p, 'C')
into STRICT		cd_cnpj_cliente_w
;

select	CASE WHEN coalesce(max(dt_atualizacao)::text, '') = '' THEN  'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	fin_cliente_inadimplente i
where	i.ie_atualizar_versao in ( 'C', 'R')
and		cd_cnpj_cliente_w = i.cd_cnpj
and		ie_situacao = 'A';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
