CREATE OR REPLACE FUNCTION man_obter_sla_leg (nr_seq_ordem_serv bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_leg_w 			bigint;
ie_tipo_sla_w			smallint;
ie_classificacao_w		varchar(2);


BEGIN

select	max(coalesce(man_obter_tipo_SLA(a.nr_sequencia), 0)) ie_tipo_sla	,
		max(ie_classificacao)
into STRICT	ie_tipo_sla_w,
		ie_classificacao_w
from	man_ordem_servico a
where	nr_sequencia = nr_seq_ordem_serv;

if (ie_tipo_sla_w = 1) then
	 nr_seq_leg_w := 8665;
end if;

if (ie_tipo_sla_w = 2) then
	 nr_seq_leg_w := 8666;
end if;

if (ie_tipo_sla_w = 3) then
	 nr_seq_leg_w := 8667;
end if;

if (ie_tipo_sla_w = 0 and ie_classificacao_w = 'E') then
	 nr_seq_leg_w := 8668;
end if;

return	nr_seq_leg_w;

end;
$body$
