CREATE OR REPLACE FUNCTION man_obter_sprint_story (nr_story_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sprint_w	desenv_sprint.nr_sequencia%type;


BEGIN

select	nr_sequencia
into STRICT	nr_sprint_w
from (	SELECT	a.nr_sequencia
	from	desenv_sprint a,
		desenv_story_sprint b
	where	b.nr_sprint = a.nr_sequencia
	and	b.nr_story = nr_story_p
	order by a.dt_inicial desc) alias0 LIMIT 1;

return	nr_sprint_w;

end;
