SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_status_exec_os ( nr_sequencia_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_seq_gerencia_des_w			GERENCIA_WHEB.NR_SEQUENCIA%Type;
qt_ativ_prevista_w			bigint;
qt_ativ_real_w				bigint;
ie_status_retorno_w			varchar(255);


BEGIN 
 
select	max(nr_seq_gerencia_des) 
into STRICT	nr_seq_gerencia_des_w 
from	man_ordem_servico_v 
where	nr_sequencia	= nr_sequencia_p;
 
if (coalesce(nr_seq_gerencia_des_w,0) <> 0) then 
	begin 
	 
	ie_status_retorno_w	:= 'NEXEC';
	select	count(*) 
	into STRICT	qt_ativ_prevista_w 
	from	man_ordem_ativ_prev a 
	where	a.nr_seq_ordem_serv	= nr_sequencia_p 
	and	trunc(a.dt_prevista)		= trunc(clock_timestamp()) 
	and	exists (	SELECT	1 
			from	grupo_desenvolvimento x, 
				usuario_grupo_des y 
			where	x.nr_sequencia		= y.nr_seq_grupo 
			and	x.nr_seq_gerencia	= nr_seq_gerencia_des_w 
			and	y.nm_usuario_grupo	= a.nm_usuario_prev);
			 
	if (qt_ativ_prevista_w > 0) then 
		begin 
		ie_status_retorno_w	:= 'NEXEC';
	 
		select	count(*) 
		into STRICT	qt_ativ_prevista_w 
		from	man_ordem_ativ_prev a 
		where	a.nr_seq_ordem_serv	= nr_sequencia_p 
		and	trunc(a.dt_prevista)	= trunc(clock_timestamp()) 
		/*and	a.nm_usuario_prev	<> 'JavaFin'*/
 
		and	not exists (	SELECT	1 
					from	man_ordem_serv_ativ y 
					where	y.nr_seq_ordem_serv	= a.nr_seq_ordem_serv 
					and	y.nr_seq_ativ_prev	= a.nr_sequencia 
					and	y.nm_usuario_exec	= a.nm_usuario_prev) 
		and	exists (	select	1 
				from	grupo_desenvolvimento x, 
					usuario_grupo_des y 
				where	x.nr_sequencia		= y.nr_seq_grupo 
				and	x.nr_seq_gerencia	= nr_seq_gerencia_des_w 
				and	y.nm_usuario_grupo	= a.nm_usuario_prev) 
		and	exists (	select	1 
				from	man_ordem_servico_exec y 
				where	y.nr_seq_ordem		= a.nr_seq_ordem_serv 
				and	y.nm_usuario_exec	= a.nm_usuario_Prev 
				and	coalesce(y.dt_fim_execucao::text, '') = '');
				 
		if (qt_ativ_prevista_w > 0) then		 
			ie_status_retorno_w	:= 'NEXEC';
		else	 
			begin 
			select	count(*) 
			into STRICT	qt_ativ_prevista_w 
			from	man_ordem_ativ_prev a 
			where	a.nr_seq_ordem_serv	= nr_sequencia_p 
			and	trunc(a.dt_prevista)		= trunc(clock_timestamp()) 
			and	exists (	SELECT	1 
						from	man_ordem_serv_ativ y 
						where	y.nr_seq_ordem_serv	= a.nr_seq_ordem_serv 
						and	y.nr_seq_ativ_prev	= a.nr_sequencia 
						and	y.nm_usuario_exec	= a.nm_usuario_prev 
						and	coalesce(y.dt_fim_atividade::text, '') = '' 
						and	trunc(y.dt_atividade) = trunc(clock_timestamp())) 
			and	exists (	select	1 
					from	grupo_desenvolvimento x, 
						usuario_grupo_des y 
					where	x.nr_sequencia		= y.nr_seq_grupo 
					and	x.nr_seq_gerencia	= nr_seq_gerencia_des_w 
					and	y.nm_usuario_grupo	= a.nm_usuario_prev);
					 
			if (qt_ativ_prevista_w > 0) then		 
				ie_status_retorno_w	:= 'EMEXEC';	
			end if;
			end;
		end if;
		end;
	end if;
 
	select	count(*) 
	into STRICT	qt_ativ_real_w 
	from	man_ordem_ativ_prev a 
	where	a.nr_seq_ordem_serv	= nr_sequencia_p 
	and	trunc(a.dt_prevista)		= trunc(clock_timestamp()) 
	and	(a.dt_real IS NOT NULL AND a.dt_real::text <> '') 
	and	a.pr_atividade		= 100 
	and	exists (	SELECT	1 
				from	man_ordem_serv_ativ y 
				where	y.nr_seq_ordem_serv	= a.nr_seq_ordem_serv 
				and	y.nm_usuario_exec	= a.nm_usuario_prev 
				and	(y.dt_fim_atividade IS NOT NULL AND y.dt_fim_atividade::text <> '') 
				and	trunc(y.dt_atividade) = trunc(clock_timestamp())) 
	and	exists (	select	1 
			from	grupo_desenvolvimento x, 
				usuario_grupo_des y 
			where	x.nr_sequencia		= y.nr_seq_grupo 
			and	x.nr_seq_gerencia	= nr_seq_gerencia_des_w 
			and	y.nm_usuario_grupo	= a.nm_usuario_prev);
	 
	if (qt_ativ_real_w > 0) then 
		ie_status_retorno_w	:= 'EXEC';
	end if;
	 
	end;
end if;
 
if (ie_opcao_p = 2) then 
	begin 
	select	CASE WHEN ie_status_retorno_w='PREV' THEN 'Prevista' WHEN ie_status_retorno_w='NEXEC' THEN 'Não executada' WHEN ie_status_retorno_w='EMEXEC' THEN 'Em execução' WHEN ie_status_retorno_w='EXEC' THEN 'Executada' END  
	into STRICT	ie_status_retorno_w 
	;
	exception when others then 
		ie_status_retorno_w	:= '';
	end;
end if;
 
return	ie_status_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION man_obter_status_exec_os ( nr_sequencia_p bigint, ie_opcao_p bigint) FROM PUBLIC;

