CREATE OR REPLACE FUNCTION man_obter_status_leg (nr_seq_ordem_serv bigint) RETURNS bigint AS $body$
DECLARE

 
nr_seq_leg_w			bigint;
ie_prioridade_w			varchar(1);
ie_novo_hist_w			varchar(1);
dt_fim_previsto_w		timestamp;
dt_reabertura_w			timestamp;
dt_fim_real_w			timestamp;
ie_status_ordem_w		varchar(1);
dt_inicio_desejado_w	timestamp;
		

BEGIN 
 
select	substr(Man_Obter_Se_Novo_Hist(a.nr_sequencia, wheb_usuario_pck.get_nm_usuario),1,3) ie_novo_hist, 
		dt_fim_previsto, 
		dt_reabertura, 
		dt_fim_real, 
		ie_status_ordem, 
		dt_inicio_desejado	 
into STRICT	ie_novo_hist_w, 
		dt_fim_previsto_w, 
		dt_reabertura_w, 
		dt_fim_real_w, 
		ie_status_ordem_w, 
		dt_inicio_desejado_w 
from	man_ordem_servico_v2 a 
where	nr_sequencia = nr_seq_ordem_serv;
 
if (ie_status_ordem_w = '1' and dt_inicio_desejado_w < clock_timestamp() and coalesce(dt_fim_previsto_w::text, '') = '' and coalesce(dt_reabertura_w::text, '') = '') then 
	nr_seq_leg_w := 8678;	-- Aberta 
end if;
 
if (dt_fim_previsto_w <> clock_timestamp() and (dt_reabertura_w IS NOT NULL AND dt_reabertura_w::text <> '')) then 
	nr_seq_leg_w := 8680;	--Reaberta 
end if;
 
if ((dt_fim_previsto_w IS NOT NULL AND dt_fim_previsto_w::text <> '') and dt_fim_previsto_w = clock_timestamp()) then 
	nr_seq_leg_w := 8679;	--fim previsto hoje 
end if;
 
if ((dt_fim_previsto_w IS NOT NULL AND dt_fim_previsto_w::text <> '') and dt_fim_previsto_w < clock_timestamp() and coalesce(dt_fim_real_w::text, '') = '' and coalesce(dt_reabertura_w::text, '') = '') then 
	nr_seq_leg_w := 8681;	 --Atrasada 
end if;
 
if (ie_novo_hist_w = 'S') then 
	nr_seq_leg_w := 8677;	-- Novo histÃ³rico 
end if;
 
return nr_seq_leg_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
