CREATE OR REPLACE FUNCTION man_obter_status_story (nr_story_p bigint, dt_referencia_p timestamp default clock_timestamp()) RETURNS bigint AS $body$
DECLARE


cd_status_w	desenv_story_sprint.cd_status%type;


BEGIN

select	coalesce(cd_status,0)
into STRICT	cd_status_w
from (SELECT	cd_status
	from	desenv_story_sprint
	where	nr_story = nr_story_p
	and	dt_atualizacao < dt_referencia_p
	order by dt_atualizacao desc) alias1 LIMIT 1;

return	cd_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
