CREATE OR REPLACE FUNCTION man_obter_status_tra_corp (nm_table_name_p text, nm_primary_key_name_p text, nr_primary_key_value_p text) RETURNS varchar AS $body$
DECLARE

--'A' = 927336 - Aguardando tradução
--'T' = 927338 - Tradução realizada
--'N' = 927454 - Tradução não configurada
nm_atributo_exp_w	varchar(100);
qt_traducao_w		integer;
ds_sql_dinamico		varchar(500);
cd_exp_w			bigint;

c1 CURSOR FOR
SELECT	nm_atributo_exp
from	corp_tabelas_traducao
where	nm_tabela = nm_table_name_p
and		coalesce(ie_situacao, 'A') <> 'I';


BEGIN
	open c1;
		loop
			fetch c1 into nm_atributo_exp_w;
				if (coalesce(nm_atributo_exp_w::text, '') = '') then
					return 'N';
				end if;
				ds_sql_dinamico := 'select '
								|| nm_atributo_exp_w
								|| ' from '
								|| nm_table_name_p
								|| ' where '
								|| nm_primary_key_name_p
								|| ' = '
								|| nr_primary_key_value_p;

				EXECUTE ds_sql_dinamico into STRICT cd_exp_w;

				select	Count(*)
				into STRICT	qt_traducao_w
				from	dic_expressao_idioma
				where	ds_locale in ( 'en_US', 'pt_BR', 'es_MX' )
				and		cd_expressao = cd_exp_w;

				if ( qt_traducao_w < 3 ) then
					return 'A';
				end if;
			EXIT WHEN NOT FOUND; /* apply on c1 */
		end loop;
	close c1;

	return 'T';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
