CREATE OR REPLACE FUNCTION man_obter_tempo_ativ_usuario ( nm_usuario_p text, dt_referencia_p timestamp, ie_tempo_p text) RETURNS bigint AS $body$
DECLARE


dt_entrada_w	timestamp;
dt_saida_w	timestamp;
qt_min_interv_w	integer;
qt_min_lanche_w	integer;
qt_tempo_w	double precision;
ie_erro_w	varchar(1) := 'N';


BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	begin
	begin
	select	dt_entrada,
		coalesce(dt_saida,clock_timestamp()),
		coalesce(qt_min_intervalo,0),
		coalesce(qt_min_lanche,0)
	into STRICT	dt_entrada_w,
		dt_saida_w,
		qt_min_interv_w,
		qt_min_lanche_w
	from	usuario_controle
	where	nm_usuario = nm_usuario_p
	and	dt_referencia = trunc(dt_referencia_p,'dd');
	exception
	when others then
		ie_erro_w := 'S';
	end;

	if (ie_erro_w = 'N') then
		begin
		qt_tempo_w := ((dt_saida_w - dt_entrada_w) * 1440) - qt_min_interv_w - qt_min_lanche_w;

		if (ie_tempo_p = 'H') then
			begin
			qt_tempo_w := dividir(qt_tempo_w,60);
			end;
		end if;
		end;
	end if;
	end;
end if;
return qt_tempo_w;
end;
$body$
