CREATE OR REPLACE FUNCTION man_obter_tempo_at_ext_usuario ( nm_usuario_p text, dt_referencia_p timestamp, ie_tempo_p text) RETURNS bigint AS $body$
DECLARE


qt_minuto_w	bigint;
qt_tempo_w	double precision;
ie_erro_w	varchar(1) := 'N';


BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	begin
	begin
	select	coalesce(sum(a.qt_minuto),0)
	into STRICT	qt_minuto_w
	from	usuario_controle c,
		usuario_atividade a
	where	c.nr_sequencia = a.nr_seq_controle
	and	c.nm_usuario = nm_usuario_p
	and	trunc(a.dt_atividade,'dd') = trunc(dt_referencia_p,'dd');
	exception
	when others then
		ie_erro_w := 'S';
	end;

	if (ie_erro_w = 'N') then
		begin
		qt_tempo_w := qt_minuto_w;

		if (ie_tempo_p = 'H') then
			begin
			qt_tempo_w := dividir(qt_tempo_w,60);
			end;
		end if;
		end;
	end if;
	end;
end if;
return qt_tempo_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
