SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_tempo_os ( nr_seq_ordem_p bigint, ie_opcao_p text, ie_tipo_retorno_p text) RETURNS bigint AS $body$
DECLARE



qt_tempo_w	double precision;

/*

ie_opcao_p
G	- Tempo total
P	- na Philips
C	- no Cliente
D	- no Desenvolvimento
T	- na Tecnologia
S	- no Suporte

ie_tipo_retorno_p
M	- Minuto
H	- Hora
D	- Dia

*/
BEGIN

if (ie_opcao_p = 'G') then

	select	sum(man_obter_tempo_estagio(a.nr_sequencia,a.nr_seq_ordem,'M'))
	into STRICT	qt_tempo_w
	from	man_estagio_processo b,
		man_ordem_serv_estagio a,
		man_ordem_servico c
	where	a.nr_seq_ordem	= nr_seq_ordem_p
	and	a.nr_seq_ordem	= c.nr_sequencia
	and	b.nr_sequencia	= a.nr_seq_estagio
	and	b.nr_sequencia <> 9
	and	a.dt_atualizacao <= coalesce(c.dt_fim_real, a.dt_atualizacao);

elsif (ie_opcao_p = 'P') then

	select	sum(man_obter_tempo_estagio(a.nr_sequencia,a.nr_seq_ordem,'M'))
	into STRICT	qt_tempo_w
	from	man_estagio_processo b,
		man_ordem_serv_estagio a,
		man_ordem_servico c
	where	a.nr_seq_ordem	= nr_seq_ordem_p
	and	a.nr_seq_ordem	= c.nr_sequencia
	and	coalesce(b.ie_aguarda_cliente,'S')	= 'N'
	and	b.nr_sequencia <> 9
	and	b.nr_sequencia	= a.nr_seq_estagio
	and	a.dt_atualizacao <= coalesce(c.dt_fim_real, a.dt_atualizacao);

elsif (ie_opcao_p = 'C') then

	select	sum(man_obter_tempo_estagio(a.nr_sequencia,a.nr_seq_ordem,'M'))
	into STRICT	qt_tempo_w
	from	man_estagio_processo b,
		man_ordem_serv_estagio a,
		man_ordem_servico c
	where	a.nr_seq_ordem	= nr_seq_ordem_p
	and	a.nr_seq_ordem	= c.nr_sequencia
	and	coalesce(b.ie_aguarda_cliente,'S')	= 'S'
	and	b.nr_sequencia	= a.nr_seq_estagio
	and	a.dt_atualizacao <= coalesce(c.dt_fim_real, a.dt_atualizacao);

elsif (ie_opcao_p = 'D') then

	select	sum(man_obter_tempo_estagio(a.nr_sequencia,a.nr_seq_ordem,'M'))
	into STRICT	qt_tempo_w
	from	man_estagio_processo b,
		man_ordem_serv_estagio a,
		man_ordem_servico c
	where	a.nr_seq_ordem	= nr_seq_ordem_p
	and	a.nr_seq_ordem	= c.nr_sequencia
	and	coalesce(b.ie_aguarda_cliente,'S')	= 'N'
	and	b.ie_desenv	= 'S'
	and	b.nr_sequencia <> 9
	and	b.nr_sequencia	= a.nr_seq_estagio
	and	a.dt_atualizacao <= coalesce(c.dt_fim_real, a.dt_atualizacao);

elsif (ie_opcao_p = 'T') then

	select	sum(man_obter_tempo_estagio(a.nr_sequencia,a.nr_seq_ordem,'M'))
	into STRICT	qt_tempo_w
	from	man_estagio_processo b,
		man_ordem_serv_estagio a,
		man_ordem_servico c
	where	a.nr_seq_ordem	= nr_seq_ordem_p
	and	a.nr_seq_ordem	= c.nr_sequencia
	and	coalesce(b.ie_aguarda_cliente,'S')	= 'N'
	and	b.ie_tecnologia	= 'S'
	and	b.nr_sequencia <> 9
	and	b.nr_sequencia	= a.nr_seq_estagio
	and	a.dt_atualizacao <= coalesce(c.dt_fim_real, a.dt_atualizacao);

elsif (ie_opcao_p = 'S') then

	select	sum(man_obter_tempo_estagio(a.nr_sequencia,a.nr_seq_ordem,'M'))
	into STRICT	qt_tempo_w
	from	man_estagio_processo b,
		man_ordem_serv_estagio a,
		man_ordem_servico c
	where	a.nr_seq_ordem	= nr_seq_ordem_p
	and	a.nr_seq_ordem	= c.nr_sequencia
	and	coalesce(b.ie_aguarda_cliente,'S')	= 'N'
	and	b.ie_suporte	= 'S'
	and	b.nr_sequencia <> 9
	and	b.nr_sequencia	= a.nr_seq_estagio
	and	a.dt_atualizacao <= coalesce(c.dt_fim_real, a.dt_atualizacao);

end if;

if (ie_tipo_retorno_p = 'M') then
	return	coalesce(qt_tempo_w,0);
elsif (ie_tipo_retorno_p = 'H') then
	return	coalesce(dividir(qt_tempo_w,60),0);
elsif (ie_tipo_retorno_p = 'D') then
	return	coalesce(dividir(qt_tempo_w,1440),0);
else
	return	0;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
