CREATE OR REPLACE FUNCTION man_obter_tempo_tipo_os_cli ( nr_sequencia_p bigint, nr_seq_ordem_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w			bigint := 0;
dt_atual_w			timestamp;
dt_atual_ww			timestamp;
dt_atualizacao_w			timestamp;

/*Legenda
	D - Dias corridos
	M - Minutos corridos
*/
BEGIN

/* Data de alteração do tipo (cliente) da ordem de serviço*/

select	dt_atualizacao
into STRICT	dt_atualizacao_w
from	man_ordem_serv_hist_tp_cli
where	nr_seq_ordem_servico	= nr_seq_ordem_p
and	nr_sequencia 		= nr_sequencia_p;


/* Obtem dados da Ordem de Serviço */

select	coalesce(dt_fim_real,clock_timestamp())
into STRICT	dt_atual_w
from	man_ordem_servico
where	nr_sequencia = nr_seq_ordem_p;

/* Data do próximo tipo OS para calcular a diferença*/

select	min(dt_atualizacao)
into STRICT	dt_atual_ww
from	man_ordem_serv_hist_tp_cli
where	nr_seq_ordem_servico	= nr_seq_ordem_p
and	dt_atualizacao 		> dt_atualizacao_w;

/* Se não tiver próximo tipo calcula pela data atual ou pela data de fim da ordem*/

if (coalesce(dt_atual_ww::text, '') = '') then
	dt_atual_ww	:= dt_atual_w;
end if;

if (ie_tipo_p = 'M') then
	qt_retorno_w			:= obter_min_entre_datas(dt_atualizacao_w,dt_atual_ww,1);
elsif (ie_tipo_p = 'D') then
	qt_retorno_w			:= obter_dias_entre_datas(dt_atualizacao_w,dt_atual_ww);
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
