CREATE OR REPLACE FUNCTION man_obter_tipo_alerta_regra ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_alerta_w	varchar(15);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	ie_tipo_alerta
	into STRICT	ie_tipo_alerta_w
	from	man_regra_envio_acomp
	where	nr_sequencia = nr_sequencia_p;
end if;

return ie_tipo_alerta_w;

end;
