CREATE OR REPLACE FUNCTION man_obter_tipo_hist_equip_evt (ie_evento_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint;

c01 CURSOR FOR
SELECT	nr_seq_tipo_hist
from	regra_tipo_hist_equip_evt
where	ie_evento = ie_evento_p
and	trunc(clock_timestamp()) between trunc(coalesce(dt_inicio_vigencia,clock_timestamp())) and trunc(coalesce(dt_fim_vigencia,clock_timestamp()))
order by nr_sequencia;


BEGIN

if (coalesce(ie_evento_p,'0') <> '0') then
	begin
	open C01;
	loop
	fetch C01 into
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;

	end;
end if;

return	ds_retorno_w;

end;
