CREATE OR REPLACE FUNCTION man_obter_tipo_hist_grupo ( nr_seq_ordem_p bigint, nr_seq_tipo_p bigint) RETURNS varchar AS $body$
DECLARE



nr_grupo_trabalho_w		bigint;
nr_grupo_planejamento_w		bigint;
qt_existe_w			bigint;
ds_retorno_w			varchar(1)	:= 'S';


BEGIN
select	count(*)
into STRICT	qt_existe_w
from	man_tipo_hist
where ((nr_seq_grupo_trabalho IS NOT NULL AND nr_seq_grupo_trabalho::text <> '') or
	(nr_seq_grupo_planejamento IS NOT NULL AND nr_seq_grupo_planejamento::text <> ''));

if (qt_existe_w > 0) then
	begin
	select	coalesce(max(nr_grupo_trabalho), 0)
	into STRICT	nr_grupo_trabalho_w
	from	man_ordem_servico
	where	nr_sequencia	= nr_seq_ordem_p;

	select	coalesce(max(nr_grupo_planej), 0)
	into STRICT	nr_grupo_planejamento_w
	from	man_ordem_servico
	where	nr_sequencia	= nr_seq_ordem_p;

	if (nr_grupo_trabalho_w > 0) then
		begin
		select	count(*)
		into STRICT	qt_existe_w
		from	man_tipo_hist
		where	nr_sequencia		= nr_seq_tipo_p
		and	coalesce(nr_seq_grupo_trabalho, nr_grupo_trabalho_w)	= nr_grupo_trabalho_w;

		if (qt_existe_w = 0) then
			ds_retorno_w	:= 'N';
		end if;

		end;
	end if;
	if (nr_grupo_planejamento_w > 0) then
		begin
		select	count(*)
		into STRICT	qt_existe_w
		from	man_tipo_hist
		where	nr_sequencia		= nr_seq_tipo_p
		and	coalesce(nr_seq_grupo_planejamento, nr_grupo_planejamento_w)	= nr_grupo_planejamento_w;

		if (qt_existe_w = 0) then
			ds_retorno_w	:= 'N';
		end if;

		end;
	end if;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
