CREATE OR REPLACE FUNCTION man_obter_tipo_risco ( nr_seq_reg_tipo_risco_p reg_tipo_risco.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_reg_tipo_risco_w	reg_tipo_risco.ds_tipo_risco%type;


BEGIN

select	coalesce(max(rtr.ds_tipo_risco), 'N/A')
into STRICT	ds_reg_tipo_risco_w
from	reg_tipo_risco rtr
where	rtr.nr_sequencia  = nr_seq_reg_tipo_risco_p
order by 1;

return ds_reg_tipo_risco_w;

end;
