SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_tit_os ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := substr(wheb_mensagem_pck.get_texto(308435),1,255);/*Ordem de Serviço*/
ie_oficializado_w	varchar(1);
ie_funcao_w		varchar(1) := 'N';

nr_seq_local_w		bigint;
nr_seq_cliente_w	bigint;
cd_funcao_w		integer;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	com_cliente a
where	a.cd_cnpj = (	select	max(coalesce(b.cd_cnpj,'0'))
			from	man_localizacao b
			where	b.nr_sequencia = nr_seq_local_w);

BEGIN
select	max(coalesce(a.nr_seq_localizacao,0)),
	max(coalesce(a.cd_funcao,0))
into STRICT	nr_seq_local_w,
	cd_funcao_w
from	man_ordem_servico a
where	a.nr_sequencia = nr_sequencia_p;

open C01;
loop
fetch C01 into
	nr_seq_cliente_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_oficializado_w
	from	com_cli_ofic_uso a,
		com_cli_ofic_uso_mod b,
		com_cli_ofic_uso_mod_func c
	where	a.nr_sequencia		= b.nr_seq_ofic
	and	b.nr_sequencia		= c.nr_seq_mod_ofic_uso
	and	c.cd_funcao		= cd_funcao_w
	and	a.nr_seq_cliente	= nr_seq_cliente_w;

	if (ie_oficializado_w = 'S') then
		ie_funcao_w := 'S';
	end if;

	end;
end loop;
close C01;

if (coalesce(ie_funcao_w,'N') = 'N') then
	ds_retorno_w := substr(wheb_mensagem_pck.get_texto(1006815),1,255);  /*Ordem de Serviço (Função não oficializada para este cliente)*/
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
