CREATE OR REPLACE FUNCTION man_obter_ultimo_estagio_os (nr_ordem_servico_p bigint, dt_atualizacao_p timestamp, nm_usuario_p text, ie_tipo_estagio_p text) RETURNS varchar AS $body$
DECLARE


/* Tipo de estagio

	U - Ultimo colocado pelo usu√°rio no dia
	D - Ultimo da OS no dia (independente do usuario) */
ds_estagio_w		varchar(255);
nr_seq_estagio_w	bigint;

c01 CURSOR FOR
	SELECT	a.nr_seq_estagio,
		substr(obter_desc_expressao(b.cd_exp_estagio,b.ds_estagio),1,40)
	from	man_estagio_processo b,
		man_ordem_serv_estagio a
	where	a.nr_seq_ordem		= nr_ordem_servico_p
	and	a.nr_seq_estagio	= b.nr_sequencia
	and (ie_tipo_estagio_p = 'D' or (a.nm_usuario		= nm_usuario_p))
	and	a.dt_atualizacao between trunc(dt_atualizacao_p, 'dd') and
			trunc(dt_atualizacao_p, 'dd') + 86399/86400
	order by a.dt_atualizacao;


BEGIN

open	c01;
loop
fetch	c01 into
	nr_seq_estagio_w,
	ds_estagio_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	nr_seq_estagio_w	:= nr_seq_estagio_w;

	end;
end loop;
close c01;

return	ds_estagio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
