CREATE OR REPLACE FUNCTION man_obter_ultimo_result_teste ( nr_caso_teste_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_resultado_w		varchar(1);
ds_resultado_w		varchar(255);
			

BEGIN

select	max(ie_resultado),
	max(ds_resultado)
into STRICT	ie_resultado_w,
	ds_resultado_w
from (	SELECT	ie_resultado,
		ds_resultado
	from	desenv_caso_teste_result
	where	nr_caso_teste = nr_caso_teste_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	order by dt_atualizacao desc) alias3 LIMIT 1;

if (ie_opcao_p = 'D') then
	return ds_resultado_w;
else
	return ie_resultado_w;
end if;

end;
