CREATE OR REPLACE FUNCTION man_obter_usuario_lib_aprov_os ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(15);

/*	L - Usuário liberação
	A - Usuário Aprovação	*/
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	begin
	if (ie_opcao_p = 'L') then
		begin
		select	max(b.nm_usuario)
		into STRICT	ds_retorno_w
		from	usuario b,
			man_ordem_servico a
		where	a.cd_pessoa_solicitante = b.cd_pessoa_fisica
		and	a.nr_sequencia = nr_sequencia_p
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');
		end;
	elsif (ie_opcao_p = 'A') then
		begin
		select	max(b.nm_usuario)
		into STRICT	ds_retorno_w
		from	usuario b,
			man_ordem_servico_aprov a
		where	a.cd_pessoa_aprov = b.cd_pessoa_fisica
		and	a.nr_seq_ordem_serv = nr_sequencia_p
		and	a.dt_aprovacao = (	SELECT	max(x.dt_aprovacao)
					from	man_ordem_servico_aprov x
					where	x.nr_seq_ordem_serv = nr_sequencia_p);
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
