CREATE OR REPLACE FUNCTION man_obter_usu_resp_setor_pf ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nm_usuario_w		varchar(15);


BEGIN

if (coalesce(cd_pessoa_fisica_p,'0') <> '0') then
	begin
	select	obter_usuario_ativo_pf(b.cd_pessoa_resp)
	into STRICT	nm_usuario_w
	from	setor_atendimento b,
		usuario a
	where	b.cd_setor_atendimento = a.cd_setor_atendimento
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.ie_situacao = 'A'  LIMIT 1;
	exception
	when others then
		nm_usuario_w := null;
	end;
end if;

return	nm_usuario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
