CREATE OR REPLACE FUNCTION man_obter_valor_contrato_equip ( nr_sequencia_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ie_periodo_valor_p text, vl_contrato_p bigint) RETURNS bigint AS $body$
DECLARE


vl_total_w	double precision	:= 0;
ie_periodo_valor_w	varchar(15) 	:= ie_periodo_valor_p;
vl_contrato_w	double precision 	:= vl_contrato_p;
dt_inicio_w	timestamp 		:= dt_inicio_p;
dt_fim_w		timestamp 		:= dt_fim_p;


BEGIN
if (coalesce(ie_periodo_valor_w,'0') = '0') or (coalesce(vl_contrato_w,0) = 0) then
	select	ie_periodo_valor,
		vl_contrato,
		dt_inicio_contrato,
		dt_fim_contrato
	into STRICT	ie_periodo_valor_w,
		vl_contrato_w,
		dt_inicio_w,
		dt_fim_w
	from	man_contrato_manut
	where	nr_sequencia = nr_sequencia_p;
end if;

if (coalesce(ie_periodo_valor_w,'0') <> '0') and (coalesce(vl_contrato_w,0) > 0) then
	begin
	if (ie_periodo_valor_w = 'D') then
		select	obter_dias_entre_datas(dt_inicio_w, coalesce(dt_fim_w,clock_timestamp())) * vl_contrato_w
		into STRICT	vl_total_w
		;
	elsif (ie_periodo_valor_w = 'M') then
		select	ceil(dividir(obter_dias_entre_datas(dt_inicio_w, coalesce(dt_fim_w,clock_timestamp())),30)) * vl_contrato_w
		into STRICT	vl_total_w
		;
	end if;
	end;
end if;

return	vl_total_w;

end;
$body$
