CREATE OR REPLACE FUNCTION man_ordem_servico_newrecord (nm_usuario_p text, CD_SETOR_P bigint, cd_solicitante_P bigint) RETURNS bigint AS $body$
DECLARE


C01 CURSOR FOR
	SELECT  nr_sequencia
		from 	man_localizacao
		where 	cd_setor = CD_SETOR_P
		and 	ie_situacao = 'A'
		order by coalesce(nr_seq_apres,0)
		desc, nr_sequencia;
C02 CURSOR FOR
	SELECT	nr_sequencia nr_seq_local
		FROM	man_localizacao
		WHERE	cd_setor = obter_setor_usuario_pf(cd_solicitante_P)
		AND		ie_situacao = 'A'
		ORDER BY coalesce(nr_seq_apres,0) DESC, nr_sequencia;

nr_sequencia_w				integer;
ie_localizacao_usuario_w 	varchar(1);
retorno_w					integer;


BEGIN
ie_localizacao_usuario_w := Obter_Param_Usuario(4000, 117, Obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_localizacao_usuario_w);

if (ie_localizacao_usuario_w = 'S') then
	select  count(*) qt_reg
	into STRICT	retorno_w
	from 	man_localizacao
	where 	cd_setor = CD_SETOR_P
	and 	ie_situacao = 'A';

	if (retorno_w > 0) then
		open C01;
		loop
		fetch C01 into
			nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		end loop;
		close C01;
	end if;

elsif (ie_localizacao_usuario_w = 'U') then

	SELECT	coalesce(MAX(a.nr_sequencia),0)  nr_seq_local
	into STRICT 	nr_sequencia_w
	FROM	man_localizacao a,
			man_localizacao_usu_exec b
	WHERE	b.nr_seq_localizacao = a.nr_sequencia
	AND		b.nm_usuario_exec = nm_usuario_p
	AND		b.ie_situacao = 'A'
	AND		b.ie_localizacao_padrao_usu = 'S'
	AND		a.ie_situacao = 'A';

elsif (ie_localizacao_usuario_w = 'P')  then
	open C02;
	loop
	fetch C02 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	end loop;
	close C02;
end if;

return	nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
