CREATE OR REPLACE FUNCTION man_se_necessita_hist_rev_trad ( nr_seq_ordem_serv_p bigint, nm_usuario_p text ) RETURNS varchar AS $body$
DECLARE

 
ie_necessita_rev_w		varchar(1) := 'N';
nr_seq_idioma_w			bigint;


BEGIN 
 
	select	coalesce(max(c.nr_seq_idioma), 0) 
	into STRICT	nr_seq_idioma_w 
	from	man_ordem_servico a, 
			man_localizacao b, 
			pessoa_juridica c 
	where	a.nr_seq_localizacao = b.nr_sequencia 
	and		b.cd_cnpj = c.cd_cgc 
	and		a.nr_sequencia = nr_seq_ordem_serv_p 
	and		c.nr_seq_idioma <> 1;
 
	if (nr_seq_idioma_w <> 0) then 
 
		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END  
		into STRICT	ie_necessita_rev_w 
		from	cadastro_revisor_historico a 
		where	a.nr_seq_idioma = nr_seq_idioma_w 
		and		a.cd_pessoa_fisica = obter_pf_usuario(nm_usuario_p, 'C');
 
	end if;
	 
return ie_necessita_rev_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
