CREATE OR REPLACE FUNCTION matrix_obter_cor_pele (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_cor_w		varchar(80);
cd_cor_w		varchar(1);


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	begin
	select	upper(ds_cor_pele)
	into STRICT	ds_cor_w
	from	cor_pele
	where	nr_sequencia	= nr_sequencia_p;
	end;

	if (ds_cor_w like upper('%branc%')) then
        	cd_cor_w := 'B';
	elsif (ds_cor_w like upper('%negr%')) then
       		cd_cor_w := 'N';
	elsif (ds_cor_w like upper('%amarel%')) then
       	      	cd_cor_w := 'A';
	elsif (ds_cor_w like upper('%ndigen%')) then
       	      	cd_cor_w := 'I';
	elsif (ds_cor_w like upper('%nd√≠gen%')) then
       	      	cd_cor_w := 'I';
	elsif (ds_cor_w like upper('%pard%')) then
       	      	cd_cor_w := 'P';
	else
		cd_cor_w := 'S';
	end if;
else	cd_cor_w := 'S';
end if;

return	cd_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
