CREATE OR REPLACE FUNCTION matrix_validar_autenticacao (nr_prescricao_p bigint, usuario_p text, senha_p text, sis_integracao_p text) RETURNS bigint AS $body$
DECLARE

retorno integer;

--1 Encontrou informação

--0 Não encontrou informação
BEGIN

select CASE WHEN count(1)=0 THEN 0  ELSE 1 END
into STRICT   retorno
from   lab_parametro lp
inner  join prescr_medica pm on pm.cd_estabelecimento = lp.cd_estabelecimento
where  pm.nr_prescricao = nr_prescricao_p
and    lp.DS_USUARIO_MATRIX = usuario_p
and    lp.ds_senha_matrix = senha_p
and    lp.DS_SISTEMA_EXT_MATRIX = sis_integracao_p;


return retorno;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
