SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mdc_obter_profissional_escala ( ie_escala_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

nm_tabela_w         varchar(255);
ds_retorno_w        varchar(255);
cd_pessoa_fisica_w  varchar(255);


BEGIN

nm_tabela_w := obter_tabela_escala(ie_escala_p);

case nm_tabela_w
	when 'ESCALA_SAPS' then
		select cd_profissional
        into STRICT cd_pessoa_fisica_w 
        from escala_saps 
        where nr_sequencia = nr_sequencia_p;
	when 'TISS_INTERV_TERAPEUTICA_10' then
		select cd_profissional
        into STRICT cd_pessoa_fisica_w 
        from tiss_interv_terapeutica_10
        where nr_sequencia = nr_sequencia_p;
end case;

ds_retorno_w := obter_nome_pf(cd_pessoa_fisica_w);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
