CREATE OR REPLACE FUNCTION mdc_obter_total_horas_vm ( nr_seq_episodio_p mdc_episodio_pac_resp_mec.nr_seq_episodio%type) RETURNS bigint AS $body$
DECLARE


total_horas_w       bigint;
total_minutos_w     bigint;


BEGIN

select  coalesce(sum(x.qt_minutos_total), 0)
into STRICT    total_minutos_w
from    mdc_episodio_pac_resp_mec x
where   nr_seq_episodio = nr_seq_episodio_p
and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and     coalesce(dt_inativacao::text, '') = '';

total_horas_w:= obter_arredondamento_mat(to_char(total_minutos_w/60));

return total_horas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
