CREATE OR REPLACE FUNCTION mdc_obter_total_proc_agrup_log (nr_seq_mdc_proc_agrup_p bigint) RETURNS bigint AS $body$
DECLARE

		
qt_ponto_escala_w	bigint;
qt_dia_desmame_w	bigint;
qt_dias_uti_w		bigint;
qt_total_w		double precision;
		

BEGIN

select	coalesce(sum(qt_criterio),0)
into STRICT	qt_ponto_escala_w
from	mdc_proc_agrup_log
where	nr_seq_mdc_proc_agrup = nr_seq_mdc_proc_agrup_p
and	ie_criterio		= 'PE';

select	coalesce(sum(qt_criterio),0)
into STRICT	qt_dia_desmame_w
from	mdc_proc_agrup_log
where	nr_seq_mdc_proc_agrup = nr_seq_mdc_proc_agrup_p
and	ie_criterio		= 'DVENT';

select	coalesce(sum(qt_criterio),0)
into STRICT	qt_dias_uti_w
from	mdc_proc_agrup_log
where	nr_seq_mdc_proc_agrup = nr_seq_mdc_proc_agrup_p
and	ie_criterio		= 'DUTI';

qt_total_w := qt_ponto_escala_w + qt_dia_desmame_w;

if (qt_dias_uti_w > 0) then
	qt_total_w	:= qt_total_w * qt_dias_uti_w;
end if;

return qt_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION mdc_obter_total_proc_agrup_log (nr_seq_mdc_proc_agrup_p bigint) FROM PUBLIC;

