CREATE OR REPLACE FUNCTION md_obter_isolamento_paci (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_isolamento_w	varchar(1);

BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	select	coalesce(max(a.ie_paciente_isolado),'N') 
	into STRICT	ie_isolamento_w 
	from	atendimento_paciente a 
	where	a.nr_atendimento = nr_atendimento_p;
end if;
 
return	ie_isolamento_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
