SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION med_ajustar_nome_pac (nm_paciente_p text) RETURNS varchar AS $body$
DECLARE


ds_sobrenome_w  varchar(255);
ds_nome_w  	varchar(255);
achou		boolean;
i     		integer;


BEGIN
ds_sobrenome_w		:= '';
ds_nome_w		:= '';
achou			:= FALSE;

if (nm_paciente_p IS NOT NULL AND nm_paciente_p::text <> '') then
	begin
	FOR i IN 1..length(nm_paciente_p) LOOP
 		begin
	 	if (substr(nm_paciente_p, i, 1) <> ',') and (achou = FALSE) then
			begin
  			ds_sobrenome_w	:= ds_sobrenome_w || substr(nm_paciente_p, i, 1);
			end;
		else
			achou		:= TRUE;
		end if;
		if (achou = TRUE) then
			ds_nome_w	:= ds_nome_w || substr(nm_paciente_p, i, 1);
		end if;
		end;
	END LOOP;
	end;
end if;

RETURN replace(replace(ds_nome_w || ' ' || ds_sobrenome_w, ' ,',''),', ','');

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
