CREATE OR REPLACE FUNCTION med_obter_classif_agenda_pac ( cd_pessoa_fisica_p text, cd_medico_p text) RETURNS varchar AS $body$
DECLARE

 
ds_classif_agenda_w	varchar(40);
nr_sequencia_w		bigint;

 

BEGIN 
 
select	max(a.nr_sequencia) 
into STRICT	nr_sequencia_w 
from	agenda_consulta a, 
	agenda b 
where	b.cd_pessoa_fisica = cd_medico_p 
and	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
and	a.cd_agenda 	  = b.cd_agenda 
and	trunc(a.dt_agenda,'dd') <= trunc(clock_timestamp(),'dd');
 
 
select	max(DS_CLASSIF_AGENDA_TASY) 
into STRICT	ds_classif_agenda_w 
from	agenda_consulta_v 
where	nr_sequencia	= nr_sequencia_w;
 
return ds_classif_agenda_w;
 
end;
