CREATE OR REPLACE FUNCTION med_obter_cor_classif_agecons ( ie_classif_agenda_p text, ie_cor_p text, nm_usua_rio_p text) RETURNS varchar AS $body$
DECLARE

ds_cor_w		varchar(10);
ds_cor_fundo_w	varchar(10);
ds_cor_fonte_w 	varchar(10);
cd_medico_w	varchar(10);
nm_usuario_w	varchar(15);


BEGIN

nm_usuario_w	:= wheb_usuario_pck.get_nm_usuario;

if (coalesce(ie_classif_agenda_p,'XXX') <> 'XXX') and (coalesce(ie_cor_p,'XXX') <> 'XXX')	then

	select	max(cd_pessoa_fisica)
	into STRICT	cd_medico_w
	from	usuario
	where	nm_usuario = nm_usuario_w;


	select	max(ds_cor_fundo),
		max(ds_cor_fonte)
	into STRICT	ds_cor_fundo_w,
		ds_cor_fonte_w
	from	med_classif_agenda
	where 	ie_classif_agenda = ie_classif_agenda_p
	and	cd_medico = cd_medico_w;

	if (ie_cor_p = 'B') then
		ds_cor_w := ds_cor_fundo_w;
	elsif (ie_cor_p = 'F') then
		ds_cor_w := ds_cor_fonte_w;
	end if;
end if;

return	ds_cor_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
