CREATE OR REPLACE FUNCTION med_obter_cor_classif_agenda ( nr_seq_classif_med_w bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_cor_w	varchar(10);
ds_cor_fundo_w 	varchar(10);
ds_cor_fonte_w 	varchar(10);


BEGIN

if (nr_seq_classif_med_w IS NOT NULL AND nr_seq_classif_med_w::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	begin

	select	ds_cor_fonte,
		ds_cor_fundo
	into STRICT	ds_cor_fonte_w,
		ds_cor_fundo_w
	from	med_classif_agenda
	where	nr_sequencia = nr_seq_classif_med_w;

	if (ie_opcao_p = 'B') then
		ds_cor_w := ds_cor_fundo_w;
	elsif (ie_opcao_p = 'F') then
		ds_cor_w := ds_cor_fonte_w;
	end if;
	end;
end if;

return	ds_cor_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
