CREATE OR REPLACE FUNCTION med_obter_data_agenda_futura (cd_agenda_p bigint, dt_atual_p timestamp, cd_pessoa_fisica_p text) RETURNS timestamp AS $body$
DECLARE


dt_agenda_w	timestamp;


BEGIN

select	max(dt_agenda)
into STRICT	dt_agenda_w
from	agenda_consulta
where	cd_agenda		= cd_agenda_p
and	cd_pessoa_fisica	= cd_pessoa_fisica_p
